
DROP TABLE IF EXISTS `agendamento`;
CREATE TABLE `agendamento` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `data` datetime DEFAULT NULL,
  `concluido` int DEFAULT NULL,
  `pronto` int DEFAULT NULL,
  `horaChegada` datetime DEFAULT NULL,
  `metodo_pagamento` enum('dinheiro','pix','credito','debito','pendente','pacote_semanal_1','pacote_semanal_2','pacote_semanal_3','pacote_semanal_4','pacote_quinzenal') CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'pendente',
  `horaSaida` datetime DEFAULT NULL,
  `taxi_dog` tinyint DEFAULT '0',
  `taxa_taxi_dog` decimal(10,2) DEFAULT NULL,
  `donoId` int DEFAULT NULL,
  `pacote_quinzenal` tinyint(1) DEFAULT '0',
  `pacote_semanal` tinyint(1) DEFAULT '0',
  `status` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT 'aguardando',
  `pet_id` int DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=617 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `servico`;
CREATE TABLE `servico` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `nome` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `descricao` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `valor` decimal(10,0) DEFAULT NULL,
  `tipo` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'clinica',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=51 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `pet`;
CREATE TABLE `pet` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `nome` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `especie` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `idade` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `dono_id` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `sexo` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `raca` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `porte` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `observacoes` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `peso` decimal(5,2) DEFAULT NULL,
  `castrado` tinyint(1) NOT NULL DEFAULT '0',
  `tipo` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `data_cadastro` datetime DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=409 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `agendamento_clinica`;
CREATE TABLE `agendamento_clinica` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `data` datetime NOT NULL,
  `hora` time DEFAULT NULL,
  `procedimento` varchar(255) DEFAULT NULL,
  `status` varchar(20) DEFAULT 'aguardando',
  `observacoes` text,
  `pet_id` int DEFAULT NULL,
  `dono_id` int DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `agendamento_pet_servico`;
CREATE TABLE `agendamento_pet_servico` (
  `agendamentoId` int NOT NULL,
  `petId` int NOT NULL,
  `servicoId` int NOT NULL,
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `status` varchar(20) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=122 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `bandeira_cartao_ref`;
CREATE TABLE `bandeira_cartao_ref` (
  `id` int NOT NULL AUTO_INCREMENT,
  `nome` varchar(100) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `nome` (`nome`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `cliente`;
CREATE TABLE `cliente` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `nome` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `email` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `telefone` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `whatsapp` varchar(6) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `rua` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `numero` varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `complemento` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `bairro` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `cidade` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `cep` int DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=362 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `colaborador`;
CREATE TABLE `colaborador` (
  `id` int NOT NULL AUTO_INCREMENT,
  `nome` varchar(255) NOT NULL,
  `cargo` varchar(100) DEFAULT NULL,
  `telefone` varchar(20) DEFAULT NULL,
  `email` varchar(150) DEFAULT NULL,
  `data_admissao` date DEFAULT NULL,
  `ativo` tinyint(1) DEFAULT '1',
  `criado_em` datetime DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `veterinario`;
CREATE TABLE `veterinario` (
  `id` int NOT NULL AUTO_INCREMENT,
  `nome` varchar(255) NOT NULL,
  `email` varchar(255) DEFAULT NULL,
  `telefone` varchar(20) DEFAULT NULL,
  `especialidade` varchar(255) DEFAULT NULL,
  `estabelecimento_id` int NOT NULL DEFAULT '0',
  `crmv` varchar(45) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `consulta`;
CREATE TABLE `consulta` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `cliente_id` int NOT NULL,
  `pet_id` int NOT NULL,
  `data` date NOT NULL,
  `hora` time NOT NULL,
  `observacoes` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
  `criado_em` datetime DEFAULT CURRENT_TIMESTAMP,
  `status` enum('aguardando','atendido','cancelado') CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'aguardando',
  `anamnese` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
  `tipo` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_consulta_estabelecimento` (`estabelecimento_id`),
  KEY `idx_consulta_data` (`data`)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `documento_modelo`;
CREATE TABLE `documento_modelo` (
  `id` int NOT NULL AUTO_INCREMENT,
  `titulo` varchar(255) NOT NULL,
  `tipo` varchar(100) DEFAULT 'Outros',
  `conteudo` text NOT NULL,
  `criado_em` datetime DEFAULT CURRENT_TIMESTAMP,
  `cabecalho` text,
  `rodape` text,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `exame`;
CREATE TABLE `exame` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `pet_id` int DEFAULT NULL,
  `agendamento_id` int DEFAULT NULL,
  `descricao` text,
  `arquivo` text,
  `criado_em` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `financeiro`;
CREATE TABLE `financeiro` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `valor` decimal(10,0) DEFAULT NULL,
  `data` datetime DEFAULT NULL,
  `descricao` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `pet_id` int DEFAULT NULL,
  `pet_nome` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `origem` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `status` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `tipo` VARCHAR(20) NOT NULL DEFAULT 'ENTRADA',
  `inativar` tinyint(1) NOT NULL DEFAULT '0',
  `metodo_pagamento` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `bandeira_cartao` varchar(100) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `parcelas` int DEFAULT '1',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=555 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `financeiropendente`;
CREATE TABLE `financeiropendente` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `descricao` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `valor` decimal(10,2) NOT NULL,
  `data` datetime NOT NULL,
  `pet_id` int DEFAULT NULL,
  `metodo_pagamento` enum('dinheiro','pix','credito','debito','pendente') CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT 'pendente',
  `bandeira_cartao` varchar(100) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `parcelas` int DEFAULT '1',
  `agendamento_id` int DEFAULT NULL,
  `origem` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `status` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `inativar` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=96 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `medicamentos`;
CREATE TABLE `medicamentos` (
  `id` int NOT NULL AUTO_INCREMENT,
  `nome` varchar(150) NOT NULL,
  `via` varchar(50) DEFAULT NULL,
  `concentracao` varchar(50) DEFAULT NULL,
  `descricao` text,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=104 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `hospedagem_caes`;
CREATE TABLE `hospedagem_caes` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `cliente_id` int DEFAULT NULL,
  `pet_id` int DEFAULT NULL,
  `data_entrada` date DEFAULT NULL,
  `data_saida` date DEFAULT NULL,
  `valor` decimal(10,2) DEFAULT NULL,
  `observacoes` text,
  PRIMARY KEY (`id`),
  KEY `cliente_id` (`cliente_id`),
  KEY `pet_id` (`pet_id`),
  CONSTRAINT `hospedagem_caes_ibfk_1` FOREIGN KEY (`cliente_id`) REFERENCES `cliente` (`id`),
  CONSTRAINT `hospedagem_caes_ibfk_2` FOREIGN KEY (`pet_id`) REFERENCES `pet` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `internacao`;
CREATE TABLE `internacao` (
  `id` int NOT NULL AUTO_INCREMENT,
  `data_inicio` datetime NOT NULL,
  `motivo` text,
  `status` varchar(255) NOT NULL,
  `situacao` varchar(255) DEFAULT NULL,
  `risco` varchar(255) DEFAULT NULL,
  `box` varchar(255) DEFAULT NULL,
  `alta_prevista` date DEFAULT NULL,
  `diagnostico` text,
  `prognostico` text,
  `anotacoes` text,
  `pet_id` int NOT NULL,
  `dono_id` int DEFAULT NULL,
  `veterinario_id` int DEFAULT NULL,
  `estabelecimento_id` int NOT NULL,
  PRIMARY KEY (`id`),
  KEY `pet_id` (`pet_id`),
  KEY `dono_id` (`dono_id`),
  KEY `veterinario_id` (`veterinario_id`),
  CONSTRAINT `internacao_ibfk_1` FOREIGN KEY (`pet_id`) REFERENCES `pet` (`id`) ON DELETE CASCADE,
  CONSTRAINT `internacao_ibfk_2` FOREIGN KEY (`dono_id`) REFERENCES `cliente` (`id`) ON DELETE CASCADE,
  CONSTRAINT `internacao_ibfk_3` FOREIGN KEY (`veterinario_id`) REFERENCES `veterinario` (`id`) ON DELETE SET NULL
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `internacao_evento`;
CREATE TABLE `internacao_evento` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `internacao_id` int NOT NULL,
  `pet_id` int NOT NULL,
  `tipo` enum('internacao','alta','ocorrencia','peso','prescricao','medicacao_exec','obito','cancelar') DEFAULT NULL,
  `titulo` varchar(255) NOT NULL,
  `descricao` text,
  `data_hora` datetime NOT NULL,
  `criado_em` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `status` varchar(150) NOT NULL DEFAULT 'Não aplicada',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=56 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `internacao_execucao`;
CREATE TABLE `internacao_execucao` (
  `id` int NOT NULL AUTO_INCREMENT,
  `internacao_id` int NOT NULL,
  `prescricao_id` int NOT NULL,
  `veterinario_id` int NOT NULL,
  `data_execucao` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `status` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `anotacoes` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS `internacao_prescricao`;
CREATE TABLE `internacao_prescricao` (
  `id` int NOT NULL AUTO_INCREMENT,
  `internacao_id` int NOT NULL,
  `medicamento_id` int NOT NULL,
  `descricao` text NOT NULL,
  `dose` varchar(255) DEFAULT NULL,
  `frequencia` varchar(255) DEFAULT NULL,
  `frequencia_horas` int NOT NULL DEFAULT '6',
  `duracao_dias` int NOT NULL DEFAULT '1',
  `data_hora` datetime NOT NULL,
  `criado_em` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `internacao_id` (`internacao_id`),
  KEY `medicamento_id` (`medicamento_id`),
  CONSTRAINT `internacao_prescricao_ibfk_1` FOREIGN KEY (`internacao_id`) REFERENCES `internacao` (`id`) ON DELETE CASCADE,
  CONSTRAINT `internacao_prescricao_ibfk_2` FOREIGN KEY (`medicamento_id`) REFERENCES `medicamentos` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `prontuario`;
CREATE TABLE `prontuario` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `agendamento_id` int DEFAULT NULL,
  `observacoes` text,
  `arquivos` text,
  `criado_em` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `prontuariopet`;
CREATE TABLE `prontuariopet` (
  `id` int NOT NULL AUTO_INCREMENT,
  `pet_id` int NOT NULL,
  `data` datetime NOT NULL,
  `tipo` enum('evolucao','procedimento','exame','vacina') NOT NULL,
  `descricao` text NOT NULL,
  `anexo` varchar(255) DEFAULT NULL,
  `criado_em` datetime DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `receita`;
CREATE TABLE `receita` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `pet_id` int NOT NULL,
  `data` date NOT NULL,
  `resumo` varchar(255) DEFAULT NULL,
  `cabecalho` longtext,
  `conteudo` longtext,
  `rodape` longtext,
  `criado_em` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `vacina`;
CREATE TABLE `vacina` (
  `id` int NOT NULL AUTO_INCREMENT,
  `estabelecimento_id` int NOT NULL,
  `pet_id` int NOT NULL,
  `tipo` varchar(100) COLLATE utf8mb4_general_ci NOT NULL,
  `data_aplicacao` date NOT NULL,
  `data_validade` date NOT NULL,
  `lote` varchar(100) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `criado_em` datetime DEFAULT CURRENT_TIMESTAMP,
  `atualizado_em` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `fabricante` varchar(150) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `observacoes` text COLLATE utf8mb4_general_ci,
  `veterinario_id` int DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_estabelecimento` (`estabelecimento_id`),
  KEY `idx_pet` (`pet_id`),
  CONSTRAINT `fk_vacina_pet` FOREIGN KEY (`pet_id`) REFERENCES `pet` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE caixa_movimento (
    id INT AUTO_INCREMENT PRIMARY KEY,
    estabelecimento_id INT NOT NULL,
    descricao VARCHAR(255) NOT NULL,
    valor DECIMAL(10,2) NOT NULL,
    tipo ENUM('ENTRADA', 'SAIDA') DEFAULT 'SAIDA',
    data DATETIME NOT NULL
);

CREATE TABLE venda (
  id INT AUTO_INCREMENT PRIMARY KEY,
  estabelecimento_id INT NOT NULL,
  cliente VARCHAR(255) NOT NULL,
  total DECIMAL(10,2) NOT NULL,
  troco DECIMAL(10,2) DEFAULT 0.00,
  metodo_pagamento VARCHAR(50) NOT NULL,
  bandeira_cartao VARCHAR(50) DEFAULT NULL,
  parcelas INT DEFAULT 1,
  data DATETIME DEFAULT CURRENT_TIMESTAMP,
  observacao TEXT DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE venda_item (
  id INT AUTO_INCREMENT PRIMARY KEY,
  venda_id INT NOT NULL,
  produto VARCHAR(255) NOT NULL,
  quantidade INT NOT NULL,
  valor_unitario DECIMAL(10,2) NOT NULL,
  subtotal DECIMAL(10,2) NOT NULL,
  CONSTRAINT fk_venda_item FOREIGN KEY (venda_id)
    REFERENCES venda(id)
    ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


CREATE TABLE produto (
    id INT AUTO_INCREMENT PRIMARY KEY,
    estabelecimento_id INT NOT NULL,
    nome VARCHAR(255) NOT NULL,
    preco_custo DECIMAL(10,2) NULL,
    preco_venda DECIMAL(10,2) NULL,
    estoque_atual INT DEFAULT 0,
    unidade VARCHAR(50) NULL,
    data_cadastro DATETIME NULL
);

CREATE TABLE estoque_movimento (
    id INT AUTO_INCREMENT PRIMARY KEY,
    produto_id INT NOT NULL,
    estabelecimento_id INT NOT NULL,
    quantidade INT NOT NULL,
    tipo VARCHAR(20) NOT NULL,
    origem VARCHAR(100) NULL,
    data DATETIME NOT NULL,
    observacao TEXT NULL,
    CONSTRAINT fk_produto_estoque FOREIGN KEY (produto_id)
        REFERENCES produto(id)
        ON DELETE CASCADE
